(59, 'IEC-F7. Описание блоков инвертора PSU73 и PSU75', '<p>Раздел 2.5. Описание блоков инвертора PSU73 и PSU75.</p>', '<p>Раздел 2.5. Описание блоков инвертора PSU73 и PSU75.</p>', 1345456836, 'data/files/1345456831_02-05-CB-PSU73.pdf', '02-05-CB-PSU73.pdf', 5, 0),
(60, 'IEC-F7. Схемы блока инвертора PSU73', '<p>Раздел 2.5. Схемы блока инвертора PSU73.</p>', '<p>Раздел 2.5. Схемы блока инвертора PSU73.</p>', 1345638132, 'data/files/1345637950_02-05-CB-PSU73-Sch.pdf', '02-05-CB-PSU73-Sch.pdf', 5, 0),
(61, 'IEC-F7. Схемы блока инвертора PSU75', '<p>Раздел 2.5. Схемы блока инвертора PSU75.</p>', '<p>Раздел 2.5. Схемы блока инвертора PSU75.</p>', 1345569145, 'data/files/1345569145_02-05-CB-PSU75-Sch.pdf', '02-05-CB-PSU75-Sch.pdf', 5, 0);
